(54, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-05-11 09:31:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-05-11 09:31:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'klaviyo_q2_2025', 'marketing', 'en_US', 'Tailor your marketing with data-driven automation', 'Deliver personalized experiences at scale, simplify your stack, and make your data work for you using Klaviyo’s best-in-class marketing extension for Woo. With advanced segmentation based on actual customer behavior, you can send email and SMS messages that drive revenue and increase loyalty — without adding complexity', '{}', 'pending', 'woocommerce.com', '2025-05-11 09:31:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-05-11 09:31:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woocommerce-analytics-order-attribution-promotions-december-2024', 'marketing', 'en_US', 'Discover what drives your sales', 'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.', '{}', 'pending', 'woocommerce.com', '2025-05-11 09:31:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-05-11 09:31:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-05-11 09:31:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2025-05-15 15:55:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2025-05-15 15:55:44', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'payson-retirement-2025', 'update', 'en_US', 'Payson Form Retirement', 'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.', '{}', 'pending', 'woocommerce.com', '2025-05-15 15:55:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2025-05-22 16:01:15', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'paypal_q2_2025', 'marketing', 'en_US', 'Power up sales with Pay Later messaging', 'Help increase your sales and grow cart sizes by letting customers know they have flexible payment options. Merchants see up to an 11% sales lift when they add Pay Later messaging — plus, it integrates seamlessly with your PayPal checkout.', '{}', 'pending', 'woocommerce.com', '2025-05-22 16:01:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2025-06-22 08:18:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'woopayments-multibanco-alipay-wechat-launch-2025-06-23', 'marketing', 'en_US', 'Multibanco, AliPay and WeChat now available in WooPayments', 'Multibanco, AliPay, and WeChat Pay are now available in WooPayments! Offer trusted local payment methods to customers in key global markets and boost international sales. Geo restrictions apply.', '{}', 'pending', 'woocommerce.com', '2025-06-24 19:22:08', NULL, 0, 'plain', '', 0, 0, 'info'),
(68, 'woopayments-new-lpms-2025-06-30', 'marketing', 'en_US', 'Boost global sales with more local payments', 'Support for AliPay, GrabPay, Multibanco, and WeChat Pay is now built into WooPayments in applicable regions. Enable them today to give customers around the globe the convenience of local payment methods — and boost your international sales with a few simple clicks.', '{}', 'pending', 'woocommerce.com', '2025-07-03 16:07:10', NULL, 0, 'plain', '', 0, 0, 'info'),
(69, 'tiktok-webinar-promo-july2025', 'marketing', 'en_US', 'You\'re Invited: TikTok for WooCommerce Webinar', 'Join us for a free webinar with our friends at TikTok on Tuesday, July 8, at 4 pm UTC. We\'ll cover creative tips and best practices for making content that turns views into sales.', '{}', 'pending', 'woocommerce.com', '2025-07-03 16:07:11', NULL, 0, 'plain', '', 0, 0, 'info');
